    var a = c12.x*c11.y - c11.x*c12.y;
    var b = c22.x*c11.y - c11.x*c22.y;
    var c = c21.x*c11.y - c11.x*c21.y;
    var d = c11.x*(c10.y - c20.y) + c11.y*(-c10.x + c20.x);
    var e = c22.x*c12.y - c12.x*c22.y;
    var f = c21.x*c12.y - c12.x*c21.y;
